package com.pirate.ques2_2;

public class PosNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = Integer.parseInt(args[0]);
		if(num>=0) {
			System.out.println(num + " is Positive");
		}
		
		else {
			System.out.println(num + " is Negative");
		}
	}

}
